package com.xspace.commoncenter.cache;

/**
 * 缓存涉及的常量
 * 
 * @author
 *
 */
public class CacheConstants {

    private CacheConstants() {
    }

    /**
     * 一分钟的秒数
     */
    public static final int ONE_MINUTE_S = 60;
    /**
     * 一小时的秒数
     */
    public static final int ONE_HOUR_S = 3600;
    /**
     * 一天的秒数
     */
    public static final int ONE_DAY_S = 86400;
    /**
     * 2天的秒数
     */
    public static final int TWO_DAY_S = 172800;
    /**
     * 一周（7天）的秒数
     */
    public static final int ONE_WEEK_S = 604800;
    /**
     * 一月（30天）的秒数
     */
    public static final int ONE_MONTH_S = 2592000;

}
