mapper.insert:

insert into t_park (id, grid_code, manager_code, 
  code, `name`, city_code, 
  city_name, area_code, area_name, 
  address, building_num, logo_src
  )
values (#{id,jdbcType=INTEGER}, #{gridCode,jdbcType=VARCHAR}, #{managerCode,jdbcType=INTEGER}, 
  #{code,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{cityCode,jdbcType=VARCHAR}, 
  #{cityName,jdbcType=VARCHAR}, #{areaCode,jdbcType=VARCHAR}, #{areaName,jdbcType=VARCHAR}, 
  #{address,jdbcType=VARCHAR}, #{buildingNum,jdbcType=INTEGER}, #{logoSrc,jdbcType=VARCHAR}
  )

mapper.select:
select id, grid_code, manager_code, code as parkCode, `name` as parkName, city_code, city_name, area_code,
area_name, address, building_num, logo_src
from t_park
where  grid_code=#{gridCode}
and    manager_code=#{managerCode}
